import './App.css';
import { BsPeopleFill, BsFillTelephoneFill, BsCircleHalf } from "react-icons/bs"
import React, { useEffect, useState } from 'react';


function App() {
  const [data, setData] = useState({ "response_code": 0, "results": [{ "category": "", "type": "multiple", "difficulty": "easy", "question": "", "correct_answer": "", "incorrect_answers": ["", "", ""] }] })
  const [level, setlevel] = useState("easy")
  const [questionNumber, setquestionNumber] = useState(0)
  const url = `https://opentdb.com/api.php?amount=4&difficulty=${level}&type=multiple`

  const [answers, setAnswers] = useState([0, 0, 0, 0]);


  useEffect(() => {

    fetchData();
    randomAnswer()
  }, [level]);

    const randomAnswer = () => {
      let answerIndex = Math.floor(Math.random() * 4)
      let tempAnswers = [0, 0, 0, 0]
      let incorrect_answersIndex = 0
      for (let i = 0; i < 4; i++) {
        if (i == answerIndex) {
          console.log( data.results[i].correct_answer)
          //tempAnswers[i] = data.results[i].correct_answer
        } else {
          console.log( data.results[i].correct_answer)

//          tempAnswers[i] = data.results[i].incorrect_answers[incorrect_answersIndex]
          incorrect_answersIndex++
        }

      }
      setAnswers(tempAnswers)
    } 

  const fetchData = async () => {
    return await fetch(url)
      .then((res) => res.json())
      .then((d) => { setData(d); }).catch((err)=>console.log(err))

  }

  const levels = [["1,000,000", "12"], ["500,000", "11"], ["200,000", "10"], ["100,000", "9"], ["50,000", "8"], ["25,000", "7"], ["15,000", "6"], ["10,000", "5"], ["7,000", "4"], ["5,000", "3"], ["2,000", "2"], ["1,000", "1"]]

  const onAnswer = (e) => {

    if (questionNumber === 3 && level === "hard") {
      alert("game done")
    }
    if (questionNumber < 3) {
      setquestionNumber(questionNumber + 1);
    } else {
      if (level === "easy") {
        setlevel("medium")
        setquestionNumber(0)
      } else if (level === "medium") {
        setlevel("hard")
        setquestionNumber(0)
      }else{

      }
            //check is it correct

     /*  fetchData(); */
    }
  }

  return (
    <>
      <div class="wrapper">
        <svg>
          <text x="50%" y="50%" dy=".35em" text-anchor="middle">
            QUIZILLA!
          </text>
        </svg>
      </div>
      <div class="flex md:px-20 gap-5 sm:px-0 ">
        {/* JOKERS AND LEVELS */}
        <div class="w-1/4 p-4 border-r">
          <div class="mb-4">
            <div class="flex  justify-around flex-row mb-2">
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsPeopleFill color='white' size={32} />
              </button>
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsFillTelephoneFill color='white' size={32} />
              </button>
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsCircleHalf color="white" size={32} />
              </button>
            </div>
            <ul>
              {levels.map((e, i) => {
                return <li key={i} class="flex justify-between items-center mb-2">
                  <span className={(e[1] === "1" || e[1] === "7" || e[1] === "12" ? 'text-orange-500' : "")}>Level {e[1]}</span>
                  <span className={(e[1] === "1" || e[1] === "7" || e[1] === "12" ? 'text-orange-500' : "")}>${e[0]}</span>
                </li>
              })}

            </ul>
          </div>
        </div>
        {/* QUESTION LAYOUT */}
        <div class="w-3/4 p-4">
          <div class="mt-8">
            <div class="mb-6">
              <h3 class="text-xl font-semibold">Question:</h3>
              <p>
                {data.results[questionNumber].question}
              </p>
              <div class="flex space-x-2">
                <span class="bg-gray-600 text-white py-2 px-3 rounded-full text-sm m-2">{data.results[questionNumber].difficulty.toUpperCase()}</span>
                <span class="bg-gray-600 text-white py-2 px-3 rounded-full text-sm m-2">{data.results[questionNumber].category.toUpperCase()}</span>
              </div>
            </div>

            <div class="grid grid-cols-2 gap-9">
              {[...Array(4)].map((e, i) => (
                <button key={i} onClick={e => onAnswer()} class="bg-white border-black    text-black font-semibold py-2  rounded-lg transition transform hover:scale-105">
                  <div className='w-full h-full'>{answers[i]} </div>
                </button>
              ))}
            </div>
          </div>

        </div>
      </div>
    </>

  );
}

export default App;
