import './App.css';
import { BsPeopleFill, BsFillTelephoneFill, BsCircleHalf } from "react-icons/bs"
import React, { useEffect, useState } from 'react';


function App() {
  const [data, setData] = useState({ "response_code": 0, "results": [{ "category": "Entertainment: Television", "type": "multiple", "difficulty": "easy", "question": "In the TV show &quot;Cheers&quot;, Sam Malone was a former relief pitcher for which baseball team?", "correct_answer": "Boston Red Sox", "incorrect_answers": ["New York Mets", "Baltimore Orioles", "Milwaukee Brewers"] }, { "category": "Geography", "type": "boolean", "difficulty": "easy", "question": "A group of islands is called an &#039;archipelago&#039;.", "correct_answer": "True", "incorrect_answers": ["False"] }, { "category": "Entertainment: Video Games", "type": "boolean", "difficulty": "hard", "question": "In the game &quot;Melty Blood Actress Again Current Code&quot;, you can enter Blood Heat mode in Half Moon style.", "correct_answer": "False", "incorrect_answers": ["True"] }, { "category": "Entertainment: Video Games", "type": "multiple", "difficulty": "easy", "question": "Which of these is NOT a playable character in the 2016 video game Overwatch?", "correct_answer": "Invoker", "incorrect_answers": ["Mercy", "Winston", "Zenyatta"] }, { "category": "Entertainment: Video Games", "type": "multiple", "difficulty": "medium", "question": "In &quot;Overwatch&quot;, how much health does Roadhog&#039;s &quot;Take a Breather&quot; ability recover?", "correct_answer": "300", "incorrect_answers": ["100", "200", "400"] }, { "category": "Entertainment: Video Games", "type": "multiple", "difficulty": "medium", "question": "Which of these is not a character in the game, &quot;Lethal League&quot;?", "correct_answer": "Rex", "incorrect_answers": ["Switch", "Candyman", "Sonata"] }, { "category": "History", "type": "multiple", "difficulty": "medium", "question": "Which country gifted the Statue of Liberty to the United States of America?", "correct_answer": "France", "incorrect_answers": ["Spain", "England", "Germany"] }, { "category": "Sports", "type": "multiple", "difficulty": "medium", "question": "How many premier league trophies did Sir Alex Ferguson win during his time at Manchester United?", "correct_answer": "13", "incorrect_answers": ["11", "20", "22"] }, { "category": "Mythology", "type": "multiple", "difficulty": "medium", "question": "What is the name of the Greek god of peaceful deaths?", "correct_answer": "Thanatos", "incorrect_answers": ["Tartarus", "Hades", "Moros"] }, { "category": "Entertainment: Comics", "type": "multiple", "difficulty": "hard", "question": "What are the Three Virtues of Bionicle?", "correct_answer": "Unity, Duty, Destiny", "incorrect_answers": ["Build, Play, Change", "Work, Play, Live", "Forge, Build, Fight"] }] })
  const [questionNumber, setquestionNumber] = useState(data ? 0 : null)
  const url = `https://opentdb.com/api.php?amount=4&difficulty=easy&type=multiple`
  const [answerIndex, setanswerIndex] = useState(0)

  const randomAnswer = () => {
     setanswerIndex(Math.floor(Math.random() * 4));

  }


  const levels = [["1,000,000", "12"], ["500,000", "11"], ["200,000", "10"], ["100,000", "9"], ["50,000", "8"], ["25,000", "7"], ["15,000", "6"], ["10,000", "5"], ["7,000", "4"], ["5,000", "3"], ["2,000", "2"], ["1,000", "1"]]  
  const fetchInfo = () => {

    return fetch(url)
        .then((res) => res.json())
        .then((d) => { setData(d);console.log(d); console.log(data) }) 

  }

  useEffect(() => {
    randomAnswer()
      fetchInfo(); 
  }, [])

  const onAnswer = (e)=>{
console.log(Math.floor(Math.random() * 4))
  }
  return (
    <>
      <div class="wrapper">
        <svg>
          <text x="50%" y="50%" dy=".35em" text-anchor="middle">
            QUIZ TIME!
          </text>
        </svg>
      </div>
      <div class="flex md:px-20 gap-5 sm:px-0 ">
        {/* JOKERS AND LEVELS */}
        <div class="w-1/4 p-4 border-r">
          <div class="mb-4">
            <div class="flex  justify-around flex-row mb-2">
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsPeopleFill color='white' size={32} />
              </button>
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsFillTelephoneFill color='white' size={32} />
              </button>
              <button class="flex justify-center items-center w-20 h-20 rounded-full bg-gray-800 hover:shadow-lg transition focus:outline-none">
                <BsCircleHalf color="white" size={32} />
              </button>
            </div>
            <ul>
              {levels.map((e, i) => {
                return <li class="flex justify-between items-center mb-2">
                  <span className={(e[1] === "1" || e[1] === "7" || e[1] === "12" ? 'text-orange-500' : "")}>Level {e[1]}</span>
                  <span className={(e[1] === "1" || e[1] === "7" || e[1] === "12" ? 'text-orange-500' : "")}>${e[0]}</span>
                </li>
              })}

            </ul>
          </div>
        </div>
        {/* QUESTION LAYOUT */}
        <div class="w-3/4 p-4">
          <div class="mt-8">
            <div class="mb-6">
              <h3 class="text-xl font-semibold">Question:</h3>
              <p>
                {data.results[questionNumber].question}
              </p>
              <div class="flex space-x-2">
                <span class="bg-gray-600 text-white py-2 px-3 rounded-full text-sm m-2">{data.results[questionNumber].difficulty.toUpperCase()}</span>
                <span class="bg-gray-600 text-white py-2 px-3 rounded-full text-sm m-2">{data.results[questionNumber].category.toUpperCase()}</span>
              </div>
            </div>

            <div class="grid grid-cols-2 gap-9">
              {[...Array(4)].map((e,i) => (

                <button onClick={e => onAnswer()} class="bg-white border-black    text-black font-semibold py-2  rounded-lg transition transform hover:scale-105">
                  <div className='w-full h-full'>{i==answerIndex?data.results[questionNumber].correct_answer:"a"} </div>
                </button>
              ))}


            </div>
          </div>

        </div>
      </div>
    </>

  );
}

export default App;
